
public class MethodMultiplePara {
		
	static void myMethod(String name,int age,double weight)
	{
		System.out.println(name +" is "+age+" with "+weight+" kg");
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		myMethod("shweta",24,62.22);
		myMethod("renu",26,68.99);
		myMethod("saam",20,40);
		
	}

}
